import React, { Component, useState } from 'react';
import Pods from './components/pods';
import Services from './components/services';
import Connection from './components/connection';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


// let endpoint = process.env.REACT_APP_GO_SERVER;
let endpoint = "http://localhost";

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      pods: [],
      services: [],
    };
  }

  componentDidMount() {
    fetch(endpoint + "/api/pods")
      .then((res) => res.json())
      .then((data) => {
        // to debug, just in case
        console.log(endpoint)
        this.setState({
          pods: JSON.parse(data),
          services: this.state.services,
        })
      })
      .catch(console.log)

    fetch(endpoint + "/api/services")
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          pods: this.state.pods,
          services: JSON.parse(data),
        })
      })
      .catch(console.log)
  }

  render() {
    return (
      <div>
        <div style={{ height: "100vh", display: "flex" }}>
          {/* to use this: npm install react-router-dom */}
          <Router>
            <div>
              <nav>
                <div style={{ justifySelf: "left" }}>
                  {/* to use this: npm install react-pro-sidebar */}
                  <ProSidebar>
                    <Menu iconShape="square">
                      <MenuItem>Dashboard
                      <Link to="/" />
                      </MenuItem>
                      <SubMenu title="Components">
                        <MenuItem>Pods
                        <Link to="/pods" />
                        </MenuItem>
                        <MenuItem>Services
                        <Link to="/services" />
                        </MenuItem>
                        <MenuItem>Test Connection
                        <Link to="/connection-test" />
                        </MenuItem>
                      </SubMenu>
                    </Menu>
                  </ProSidebar>
                </div>
              </nav>

              {/* A <Switch> looks through its children <Route>s and
    renders the first one that matches the current URL. */}
              <Switch>
                <Route path="/pods">
                  <Pod value={this.state}/>
                </Route>
                <Route path="/services">
                  <Service value = {this}/>
                </Route>
                <Route path="/connection-test">
                  <ConnectionTest value = {this.state}/>
                </Route>
                <Route path="/">
                  <Pod value={this.state} />
                </Route>
              </Switch>
            </div>
          </Router>

          {/* <div>
            <div style={{ display: "flex", justifyContent: "space-evenly", justifySelf: "right" }}>
              <Pods pods={this.state.pods} style={{ width: "50vh" }}></Pods>
              <Services svcs={this.state.services} style={{ width: "50vh" }}></Services>
            </div>
            <div>
              <Connection></Connection>
            </div>
          </div> */}
        </div>
      </div>
    )
  }
}

const Pod = ({props}) => {
  console.log(props)
  return (
  <div style={{ display: "flex", justifyContent: "space-evenly", justifySelf: "right" }}>
              <Pods pods={props.pods} style={{ width: "50vh" }}></Pods>
  </div>
  )
}

function Service(){
  return (
  <div style={{ display: "flex", justifyContent: "space-evenly", justifySelf: "right" }}>
    <Services svcs={this.state.services} style={{ width: "50vh" }}></Services>
  </div>  
  )
}  

function ConnectionTest(){
  return (
  <div style={{ display: "flex", justifyContent: "space-evenly", justifySelf: "right" }}>
              <Connection></Connection>
  </div>
  )
}

export default App;
