import React, {Component} from 'react';

let endpoint = "http://localhost:8080";

class App extends Component {
  state = {
    pods: []
  }

  componentDidMount() {
    fetch(endpoint + "/get-pods")
    .then(res => res.json())
    .then((data) => {
      this.setState({ pods: data })
    })
    .catch(console.log)
  }

  render () {
    return (
      <div>
        <h5>Steve Jobs</h5>
        <h6>steve@apple.com</h6>
        <p>Stay Hungry, Stay Foolish</p>
    </div>
    );
  }
}

export default App;
