import React, {Component} from 'react';
import Pods from './components/pods';
import Services from './components/services';

let endpoint = process.env.REACT_APP_GO_SERVER;

class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
        pods: [],
        services: []
    };
}

  componentDidMount() {
    fetch(endpoint + "/api/pods")
    .then((res) => res.json())
    .then((data) => {
      this.setState({ 
        pods: JSON.parse(data),
      })
    })
    .catch(console.log)

    // fetch(endpoint + "/api/services")
    // .then((res) => res.json())
    // .then((data) => {
    //   this.setState({ 
    //     services: JSON.parse(data), 
    //   })
    // })
    // .catch(console.log)
  }

  render () {
    return (
      <div>
      <Pods pods={this.state.pods}></Pods>
      {/* <Services svcs={this.state.services}></Services> */}
      </div>
    )
}
}

export default App;
