import React, {Component} from 'react';
import Pods from './components/pods';

let endpoint = "http://localhost";

class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
        pods: []
    };
}

  componentDidMount() {
    fetch(endpoint + "/pod")
    .then((res) => res.json())
    .then((data) => {
      console.log(this.state)
      this.setState({ pods: data })
      console.log(this.state)
      console.log('This is your data', data)
    })
    .catch(console.log)
  }

  render () {
    return (
      <Pods pods={this.state.pods} />
    )
}
}

export default App;
