import React, {Component} from 'react';
import Pods from './components/pods';

let endpoint = "http://localhost";

const { REACT_APP_GO_SERVER } = process.env;

class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
        pods: []
    };
}

  componentDidMount() {
    fetch(endpoint + "/api/pod")
    .then((res) => res.json())
    .then((data) => {
      console.log('GO SERVER: ' + process.env)
      this.setState({ 
        pods: JSON.parse(data), 
      })
      console.log('This is your data', JSON.parse(data))
    })
    .catch(console.log)
  }

  render () {
    return (
      <Pods pods={this.state.pods} />
    )
}
}

export default App;
