import React from 'react'

let endpoint = "http://localhost";

class Exec extends React.Component {
    constructor() {
        super();
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = { cmdOutput: "", cmdColor: "" };
    }

    refreshResult(cmdError) {
        switch (cmdError) {
            case false:
                this.setState({
                    cmdColor: "green"
                })
                break;
            case true:
                this.setState({
                    cmdColor: "red",
                });
                break;
            default:
                this.setState({
                    cmdColor: "white",
                })
                break;
        }
    }

    handleSubmit(event) {
        event.preventDefault();

        const data = new FormData(event.target);
        const podName = data.get("podName")
        const podNamespace = data.get("podNamespace")
        const command = data.get("command")

        const body = {
            "podName": podName,
            "podNamespace": podNamespace,
            "command": command
        }

        fetch(endpoint + '/api/pod-exec', {
            method: 'POST',
            body: JSON.stringify(body),
        }).then((res) => {
            res.json().then(data => {
                console.log(data)
                // switch (data) {
                //     case "true":
                //         this.refreshResult(true)
                //         break;
                //     case "false":
                //         this.refreshResult(false)
                //         break;
                //     default:
                //         break;
                // }
            })
        })
    }

    render() {
        return (
            <div>
            <div style={{ display: "flex", alignItems: 'center', justifyContent: 'center' }}>
                <form onSubmit={this.handleSubmit} style={{ verticalAlign: "true" }}>
                    <div style={{ display: "flex", alignItems: 'center', justifyContent: 'center'  }}>
                        <div>
                            <label htmlFor="pod">Enter pod name</label>
                            <br />
                            <input id="podName" name="podName" type="text" />
                            <br />

                            <label htmlFor="podns">Enter pod namespace</label>
                            <br />
                            <input id="podNamespace" name="podNamespace" type="text" />
                            <br />

                            <label htmlFor="podFrom">Enter command</label>
                            <br />
                            <input id="command" name="command" type="text" />
                            <br />

                            </div>
                            <div style={{ display: "flex", alignItems: 'center', justifyContent: 'center'  }}>

                        <button style={{
                            display: "flex",
                            justifyContent: "center",
                            height: "2vh"
                        }}>Exec command!</button>
                </div>
                </div>
                <span style={{ backgroundColor: this.state.cmdColor }}>Status</span>
                </form>
            </div>
            </div>
        );
    }
};

export default Exec
